var button=document.getElementById("slide");button.onclick=function(){sideScroll(document.getElementById("libros-scrollable"),"right",50,170,20)};var back=document.getElementById("slideBack");function sideScroll(l,e,t,o,c){scrollAmount=0;var n=setInterval(function(){"left"==e?l.scrollLeft-=c:l.scrollLeft+=c,scrollAmount+=c,scrollAmount>=o&&window.clearInterval(n)},t)}back.onclick=function(){sideScroll(document.getElementById("libros-scrollable"),"left",50,170,20)};